import Header from "../Header";
import { useAsyncValue, useParams } from "react-router-dom";
import RoomsData from "../data/RoomsData";
import { useEffect, useState } from "react";
import RoomIntro from "../RoomDTIntro"


export default function RoomDetail() {
    const { id } = useParams();
    
    const [roomData, setRoomData] = useState({});
    const room = RoomsData.filter(data => data.id == id)
    useEffect(() => {
        
        setRoomData(...room);
    }, [id])
    return (
        <>
            <Header />
            <img src="images/rooms-3.jpg"/>
            <img className="h-[18rem] w-[25rem] rounded-t-2xl" src="images/rooms-3.jpg" alt="" />
            <RoomIntro images={roomData.img} desc={roomData.description} title={roomData.title} />

            {/* <h1>room no: {roomData.name}</h1> */}
        </>
    )
}
