import Header from "../Header";
import { useParams } from "react-router-dom";
import RoomsData from "../data/RoomsData";
import { useEffect, useState } from "react";


export default function RoomDetail() {
    const { id } = useParams();

    const [roomData, setRoomData] = useState({});

    useEffect(() => {
        const room = RoomsData.filter(data => data.id == id)
        setRoomData(...room);
    },[id])
    const preimg= JSON.stringify(roomData.IMG)
    return (
        <>
        {console.log(roomData.img)
        }
            <Header />
            
            <div>
                <img src={roomData.img} alt="" />
                <div className="relative">
                    <p className="font-delafield text-9xl absolute">Miles</p>
                    <p className="font-baby absolute">Room A</p>
                </div>
            </div>

            <h1>room no: {roomData.name}</h1>

        </>
    )
}
