import Header from "../Header";
import { useState } from "react";
import { useEffect } from "react";
import RoomsData from "../data/RoomsData";
import CustomerForm from "../CustomerForm";
import { useParams} from "react-router-dom";

export default function BookingPage(){
    const { id } = useParams();
    
    const [roomData, setRoomData] = useState({});
    const room = RoomsData.filter(data => data.id == id)
    useEffect(() => {
        
        setRoomData(...room);
    }, [id])
    console.log(roomData);
    return (
        <div className="bg-[#d2eae7]">
            <Header/>
            <div className="flex justify-center mt-4">
            <div className="absolute font-delafield text-[4rem] top-[140px] left-10">
                {roomData.title}
                <img className="rounded-2xl w-[420px]" src={roomData.img} alt="" />
            </div>
            <div className="">
        
            <CustomerForm/>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-[600px] h-[600px] rotate-[135deg] -z-1 translate-x-[90px] -translate-y-[500px]">
  <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 12h-15" />
</svg>

            </div>        
            </div>
           
          
        </div>
    )
}
