class UserName extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value: '',
			length: ''
		};
		this.usernameChange = this.usernameChange.bind(this);
	}
	usernameChange(e) {
		this.setState({
			value: e.target.value,
			length: e.target.value.length,
		});
	}
	render() {
		return (
			<div className={"form-group"}>
				<label htmlFor={"userName"}>Enter your username:</label>
				{this.props.userErrorH}
				<input
					type="text"
					tabIndex={"1"}
					autoFocus={'autoFocus'}
					className="form-control"
					id="userName"
					value={this.state.value}
					onChange={this.usernameChange}
				/>
			</div>
		);
	}
}

class EmailAddress extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};
		this.emailChange = this.emailChange.bind(this);
	}
	emailChange(e) {
		this.setState({value: e.target.value});
	}

	render() {

		return (
			<div className={"form-group"}>
				<label htmlFor="emailAddress">Enter your email address:</label>
				{this.props.emailErrorH}
				<input
					type="text"
					tabIndex={"2"}
					className="form-control"
					id="emailAddress"
					value={this.state.value}
					onChange={this.emailChange}
				/>
			</div>
		);
	}
}

class ShowHidePass extends React.Component {
	showHidePass() {
		if (pwd.type === "password") {
			eyeSlash.style.display = "inline-block";
			eye.style.display = "none";
			pwd.type = "text";
			rpwd.type = "text";
		} else {
			eyeSlash.style.display = "none";
			eye.style.display = "inline-block";
			pwd.type = "password";
			rpwd.type = "password";
		}
	}
	render() {
		return (
			<span className={'float-right c-pointer p-1'} onClick={this.showHidePass}>
				<i className={'fa fa-eye'} id={'eye'} title={'Show Password'}></i>
				<i className={'fa fa-eye-slash'} id={'eyeSlash'} title={'Hide Password'} style={{display:'none'}}></i>
			</span>
		);
	}
}

class Password extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};
		this.pwdChange = this.pwdChange.bind(this);
	}
	pwdChange(e) {
		this.setState({value: e.target.value});
	}

	render() {
		return (
			<div className={"form-group"}>
				<label htmlFor="pwd">Enter your password:</label>
				<ShowHidePass/>
				{this.props.pwdErrorH}
				<input
					type="password"
					tabIndex={"3"}
					className="form-control"
					id="pwd"
					value={this.state.value}
					onChange={this.pwdChange}
				/>
			</div>
		)
	}
}

class RepeatPassword extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};
		this.rpwdChange = this.rpwdChange.bind(this);
	}
	rpwdChange(e) {
		this.setState({value: e.target.value});
	}
	render() {

		return (
			<div className={"form-group"}>
				<label htmlFor="rpwd">Re-enter your password:</label>
				{this.props.rpwdErrorH}
				<input
					type="password"
					tabIndex={"4"}
					className="form-control"
					id="rpwd"
					value={this.state.value}
					onChange={this.rpwdChange}
				/>
			</div>
		)
	}
}


class SubmitBtn extends React.Component {
	render() {

		return (
			<button
				type={'submit'}
				className={"btn btn-dark"}
			>
				Submit
			</button>
		);
	}
}


class SignupForm extends React.Component {
	constructor() {
		super();
		this.state = {
			userError: '',
			emailError: '',
			pwdError: '',
			rpwdError: ''
		};
		this.validation = this.validation.bind(this);
	}
	validation(e) {
		e.preventDefault();
		let req = 'This field is required!';
		let v = 0; // this is necessary for validation
		// username input conditions
		let user = document.getElementById('userName');
		const userRegex1 = /(\W)/g;
		const userRegex2 = /(^\d)/g;
		if (user.value.length === 0) {
			this.setState({
				userError: <div className={'error'}>{req}</div>
			});
		} else if (user.value.length < 6) {
			this.setState({
				userError: <div className={'error'}>Username must be 6 characters at least!</div>
			});
		} else if (userRegex1.exec(user.value)) {
			this.setState({
				userError: <div className={'error'}>You just can use english words and numbers!</div>
			});
		} else if (userRegex2.exec(user.value)) {
			this.setState({
				userError: <div className={'error'}>You can't start username with a number!</div>
			});
		} else {
			this.setState({
				userError: ''
			});
			v++;
		}
		// email input conditions
		let email = document.getElementById('emailAddress');
		const emailRegex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		if (email.value.length === 0) {
			this.setState({
				emailError: <div className={'error'}>{req}</div>
			});
		} else if (!emailRegex.exec(email.value)) {
			this.setState({
				emailError: <div className={'error'}>Check your address!</div>
			});
		} else {
			this.setState({
				emailError: ''
			});
			v++;
		}
		// password input conditions
		if (pwd.value.length === 0) {
			this.setState({
				pwdError: <div className={'error'}>{req}</div>
			});
		} else if (pwd.value.length < 6) {
			this.setState({
				pwdError: <div className={'error'}>Password must be 6 characters at least!</div>
			});
		} else {
			this.setState({
				pwdError: ''
			});
			v++;
		}
		// password double check input conditions
		if (rpwd.value.length === 0) {
			this.setState({
				rpwdError: <div className={'error'}>{req}</div>
			});
		} else if (pwd.value !== rpwd.value) {
			this.setState({
				rpwdError: <div className={'error'}>Passwords are not same!</div>
			});
		} else {
			this.setState({
				rpwdError: ''
			});
			v++;
		}
		// When all fields have been filled truly
		if (v === 4) {
			alert ('All fields are correct!\nServer must do something...');
		}
	};
	render() {
		const eye = document.getElementById('eye');
		const eyeSlash = document.getElementById('eyeSlash');
		let pwd = document.getElementById('pwd');
		let rpwd = document.getElementById('rpwd');
		return (
			<form onSubmit={this.validation}>
				<UserName userErrorH={this.state.userError}/>
				<EmailAddress emailErrorH={this.state.emailError}/>
				<Password pwdErrorH={this.state.pwdError}/>
				<RepeatPassword rpwdErrorH={this.state.rpwdError}/>
				<SubmitBtn/>
			</form>
		);
	}
}

ReactDOM.render(<SignupForm />, document.getElementById('reactSignUpForm'));
