import Header from "../Header";
import { useAsyncValue, useParams } from "react-router-dom";
import RoomsData from "../data/RoomsData";
import { useEffect, useState } from "react";
import RoomIntro from "../RoomDTIntro"


export default function RoomDetail() {
    const { id } = useParams();

    const [roomData, setRoomData] = useState({});

    useEffect(() => {
        const room = RoomsData.filter(data => data.id == id)
        setRoomData(...room);
    }, [id])
    return (
        <>
            {console.log(roomData.img)
            }
            <Header/>
            <div></div>
            <RoomIntro images={roomData.introimg} title={roomData.title} desc={roomData.description}/>


            {/* <h1>room no: {roomData.name}</h1> */}
        </>
    )
}
